@mcp.tool(name='RestorableTables_List', description="Show the event feed of all mutations done on all the Azure Cosmos DB Tables. This helps in scenario where table was accidentally deleted. This API requires 'Microsoft.DocumentDB/locations/restorableDatabaseAccounts/.../read' permission")
def RestorableTables_List(location: str, instanceId: str, subscriptionId: str, startTime: Optional[str] = None, endTime: Optional[str] = None):
    '''Show the event feed of all mutations done on all the Azure Cosmos DB Tables. This helps in scenario where table was accidentally deleted. This API requires 'Microsoft.DocumentDB/locations/restorableDatabaseAccounts/.../read' permission'''
    base_url = "https://management.azure.com"
    url = f"{base_url}/subscriptions/{subscriptionId}/providers/Microsoft.DocumentDB/locations/{location}/restorableDatabaseAccounts/{instanceId}/restorableTables"
    params = {}
    headers = {}
    cookies = {}
    extra_headers = os.getenv('EXTRA_HEADERS')
    if extra_headers:
        try:
            for k, v in json.loads(extra_headers).items():
                headers[k] = v
        except Exception as e:
            print('Failed to parse EXTRA_HEADERS:', e)
    auth_satisfied = False
    auth_error_msgs = []
    if not auth_satisfied:
        try:
            access_token = os.getenv('AZURE_AUTH_AUTHORIZATIONCODE_ACCESS_TOKEN')
            if not access_token:
                raise ValueError('Missing OAuth2 access token in .env: AZURE_AUTH_AUTHORIZATIONCODE_ACCESS_TOKEN')
            headers['Authorization'] = f'Bearer {access_token}'
            auth_satisfied = True
        except Exception as e:
            auth_error_msgs.append(str(e))
    if not auth_satisfied:
        raise ValueError('No valid authentication found. Details: ' + '; '.join(auth_error_msgs))
    if location is None:
        raise ValueError('Missing required path parameter: location')
    if instanceId is None:
        raise ValueError('Missing required path parameter: instanceId')
    if subscriptionId is None:
        raise ValueError('Missing required path parameter: subscriptionId')
    if startTime is not None:
        params['startTime'] = startTime
    if endTime is not None:
        params['endTime'] = endTime
    try:
        resp = requests.get(url, params=params, headers=headers, cookies=cookies)
        resp.raise_for_status()  # Raise an exception for bad status codes
        if 'application/json' in resp.headers.get('Content-Type', ''):
            return resp.json()
        else:
            return {'raw': resp.text, 'status_code': resp.status_code}
    except requests.exceptions.RequestException as e:
        raise ValueError(f'HTTP request failed: {str(e)}')
    except Exception as e:
        raise ValueError(f'Unexpected error: {str(e)}')
