@mcp.tool(name='CostAllocationRules_CreateOrUpdate', description="Create/Update a rule to allocate cost between different resources within a billing account or enterprise enrollment.")
def CostAllocationRules_CreateOrUpdate(billingAccountId: str, ruleName: str, api_version: Optional[str] = None):
    '''Create/Update a rule to allocate cost between different resources within a billing account or enterprise enrollment.'''
    base_url = "https://management.azure.com"
    url = f"{base_url}/providers/Microsoft.Billing/billingAccounts/{billingAccountId}/providers/Microsoft.CostManagement/costAllocationRules/{ruleName}"
    params = {}
    headers = {}
    cookies = {}
    extra_headers = os.getenv('EXTRA_HEADERS')
    if extra_headers:
        try:
            for k, v in json.loads(extra_headers).items():
                headers[k] = v
        except Exception as e:
            print('Failed to parse EXTRA_HEADERS:', e)
    auth_satisfied = False
    auth_error_msgs = []
    if not auth_satisfied:
        try:
            access_token = os.getenv('AZURE_AUTH_AUTHORIZATIONCODE_ACCESS_TOKEN')
            if not access_token:
                raise ValueError('Missing OAuth2 access token in .env: AZURE_AUTH_AUTHORIZATIONCODE_ACCESS_TOKEN')
            headers['Authorization'] = f'Bearer {access_token}'
            auth_satisfied = True
        except Exception as e:
            auth_error_msgs.append(str(e))
    if not auth_satisfied:
        raise ValueError('No valid authentication found. Details: ' + '; '.join(auth_error_msgs))
    if billingAccountId is None:
        raise ValueError('Missing required path parameter: billingAccountId')
    if ruleName is None:
        raise ValueError('Missing required path parameter: ruleName')
    if api_version is not None:
        params['api-version'] = api_version
    try:
        resp = requests.put(url, params=params, headers=headers, cookies=cookies)
        resp.raise_for_status()  # Raise an exception for bad status codes
        if 'application/json' in resp.headers.get('Content-Type', ''):
            return resp.json()
        else:
            return {'raw': resp.text, 'status_code': resp.status_code}
    except requests.exceptions.RequestException as e:
        raise ValueError(f'HTTP request failed: {str(e)}')
    except Exception as e:
        raise ValueError(f'Unexpected error: {str(e)}')
