@mcp.tool(name='IntegrationAccountCertificates_CreateOrUpdate', description="Creates or updates an integration account certificate.")
def IntegrationAccountCertificates_CreateOrUpdate(subscriptionId: str, resourceGroupName: str, integrationAccountName: str, certificateName: str, api_version: Optional[str] = None):
    '''Creates or updates an integration account certificate.'''
    base_url = "https://management.azure.com"
    url = f"{base_url}/subscriptions/{subscriptionId}/resourceGroups/{resourceGroupName}/providers/Microsoft.Logic/integrationAccounts/{integrationAccountName}/certificates/{certificateName}"
    params = {}
    headers = {}
    cookies = {}
    extra_headers = os.getenv('EXTRA_HEADERS')
    if extra_headers:
        try:
            for k, v in json.loads(extra_headers).items():
                headers[k] = v
        except Exception as e:
            print('Failed to parse EXTRA_HEADERS:', e)
    if subscriptionId is None:
        raise ValueError('Missing required path parameter: subscriptionId')
    if resourceGroupName is None:
        raise ValueError('Missing required path parameter: resourceGroupName')
    if integrationAccountName is None:
        raise ValueError('Missing required path parameter: integrationAccountName')
    if certificateName is None:
        raise ValueError('Missing required path parameter: certificateName')
    if api_version is not None:
        params['api-version'] = api_version
    try:
        resp = requests.put(url, params=params, headers=headers, cookies=cookies)
        resp.raise_for_status()  # Raise an exception for bad status codes
        if 'application/json' in resp.headers.get('Content-Type', ''):
            return resp.json()
        else:
            return {'raw': resp.text, 'status_code': resp.status_code}
    except requests.exceptions.RequestException as e:
        raise ValueError(f'HTTP request failed: {str(e)}')
    except Exception as e:
        raise ValueError(f'Unexpected error: {str(e)}')
