@mcp.tool(name='CalculateRefund_Post', description="Calculate the refund amount of a reservation order.: Calculate price for returning `Reservations` if there are no policy errors.\n")
def CalculateRefund_Post(reservationOrderId: str, api_version: Optional[str] = None):
    '''Calculate price for returning `Reservations` if there are no policy errors. '''
    base_url = "https://management.azure.com"
    url = f"{base_url}/providers/Microsoft.Capacity/reservationOrders/{reservationOrderId}/calculateRefund"
    params = {}
    headers = {}
    cookies = {}
    extra_headers = os.getenv('EXTRA_HEADERS')
    if extra_headers:
        try:
            for k, v in json.loads(extra_headers).items():
                headers[k] = v
        except Exception as e:
            print('Failed to parse EXTRA_HEADERS:', e)
    if reservationOrderId is None:
        raise ValueError('Missing required path parameter: reservationOrderId')
    if api_version is not None:
        params['api-version'] = api_version
    try:
        resp = requests.post(url, params=params, headers=headers, cookies=cookies)
        resp.raise_for_status()  # Raise an exception for bad status codes
        if 'application/json' in resp.headers.get('Content-Type', ''):
            return resp.json()
        else:
            return {'raw': resp.text, 'status_code': resp.status_code}
    except requests.exceptions.RequestException as e:
        raise ValueError(f'HTTP request failed: {str(e)}')
    except Exception as e:
        raise ValueError(f'Unexpected error: {str(e)}')
